    // which is lazy-loaded when the route is visited.
    component: AdminHome,
    meta: { role: ['admin'] },
    children: [
      {
        path: 'welcome',
        name: 'welcome',
        
        component: WelcomePage,
        meta: { role: ['admin','student','teacher'], nav_name: '首页'}
      },
      {
        path: 'admin',
        name: 'admin',
        component: AdminControl,
        meta: { role: ['admin'], nav_name: '个人信息录入' },
      },
