      //   component: UserinfoMaintenance,
      //   meta: { role: ['admin'] },
      // },
      {
        path: 'privateinfo',
        name: 'privateinfo',
        component: PrivateinfoMaintnce,
        meta: { role: ['admin','student','teacher'], nav_name: '个人信息' }
      },
      {
        path: 'course',
        name: 'course',
        component: CourseMaintenance,
        meta: { role: ['admin'], nav_name: '课程管理'},
      },
      {
        path: 'edu',
        name: 'edu',
        component: EduMaintenance,
        meta: { role: ['admin'], nav_name: '教务管理'},
      }
    ],
    // component: () => import(/* webpackChunkName: "about" */ '../views/StudentLogin.vue')
  },

  {
    path: '/studentHome',
    name: 'studentHome',
    redirect: '/studentHome/welcome',
    component: StudentHome,
    meta: { role: ['student'] },
    children: [
      // {
      //   path: 'welcome',
      //   name: 'welcome',
      //   component: WelcomePage,
      //   meta: { role: ['admin','student','teacher'] }
      // },
      {
        path: 'privateInfo',
        name: 'privateInfo',
        component: PrivateinfoMaintnce,
        meta: { role: ['admin','student','teacher'] }
      },
      {
        path: 'optionalCourse',
        name: 'optionalCourse',
        component: OptionalCourse,
        meta: {role: ['student']}
      }
    ]

  },

  {
    path: '/teacherHome',
    name: 'teacherHome',
    redirect: '/teacherHome/welcome',
    component: TeacherHome,
    meta: { role: ['teacher'] },
    children: [
      // {
      //   path: 'welcome',
      //   name: 'welcome',
      //   component: WelcomePage,
      //   meta: { role: ['admin','student','teacher'] }
      // },
      {
        path: 'privateInfo',
        name: 'privateInfo',
        component: PrivateinfoMaintnce,
        meta: { role: ['admin','student','teacher'] }
      },
      {
        path: 'teacherCourseMaintenance',
        name: 'teacherCourseMaintenance',
        component: TeacherCourseMaintenance,
        meta: { role: ['admin','teacher']}
      }
    ]
  },

  {
    path: '/password',
    name: 'password',
    component: ResetPassword,
    meta: { role: ['admin','student','teacher'] },
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})
